tool.command:
@REF_FASTA@
octopus '${log_level}' '${speed}' --output '${variants_out}' --threads \${GALAXY_SLOTS:-4} --reference reference.fa --reads #for $read in $reads: '${read}' #end for '${one_based_indexing}' --regions #for $region in $regions: '${region.region}' #end for --regions-file '${regions_file}' --skip-regions #for $region in $skip_regions: '${region.skip_region}' #end for --skip-regions-file '${skip_regions_file}' --samples #for $sample in $samples: '${sample.sample}' #end for --samples-file '${samples_file}' '${ignore_unmapped_contigs}' --pedigree '${pedigree}' ## Little unclear on this. Is it a text param or something else. Come back to it ## --contig-output-order '${sites_only}' '${legacy}' --regenotype '${regenotype}' --bamout '${bam_out}' --split-bam-out '${split_bamout}' --data-profile '${profile_out}'
&& cat octopus_debug.log 1>&2
